    const char *getUTF8Data() { return ascii.data; }
    uint32_t getUTF8Size() { return ascii.length; }
    uint32_t getLength() { return ascii.length; }
    const char *getUTF8DataAt(uint32_t pos) { return pos < ascii.length ? ascii.data + pos : NULL; }
