                         0,          0,       {__VA_ARGS__}};
#define DEF_VTABLE_EXT(name, tp, valtype, ...)                                                     \
    const VTable name = {sizeof(tp), valtype, VTABLE_MAGIC2, 0, BuiltInType::tp, BuiltInType::tp,  \
                         0,          0,       {__VA_ARGS__}};
